slidera = 12
sliderb = 13
GPIO.setup(slidera, GPIO.OUT)
GPIO.setup(sliderb, GPIO.OUT)
pwm = GPIO.PWM(slidera, 50)
pwm2 = GPIO.PWM(sliderb, 50)
pwm.start(0)
pwm2.start(0)

