    def botonrojo (self):
        estado  = ledRojo.is_lit
        self.slider1.setStyleSheet("color: rgb(170, 170, 255);")
        if estado:
            ledRojo.off()
        else:
            ledRojo.on()
            #self.slider1.setStyleSheet("color: rgb(170, 170, 255);")
    def botonazul (self):
        estado  = ledAzul.is_lit
        if estado:
            ledAzul.off()
        else:
            ledAzul.on()
    def primerslider (self):
        valor_slider = self.slider1.value()
        m = 0.10
        b = 2.4
        posicion = (valor_slider * m) + b
        pwm.ChangeDutyCycle(posicion)



    def segundoslider (self):
        valorsegundoslider = self.slider2.value()
        m = 0.10
        b = 2.4
        posicion = (valorsegundoslider * m) + b 
        pwm2.ChangeDutyCycle(posicion)



